        // Inisialisasi CodeMirror untuk masing-masing editor
        var htmlEditor = CodeMirror.fromTextArea(document.getElementById("html-editor"), {
            lineNumbers: true,
            mode: "htmlmixed",
            theme: "material"
        });

        var cssEditor = CodeMirror.fromTextArea(document.getElementById("css-editor"), {
            lineNumbers: true,
            mode: "css",
            theme: "material"
        });

        var jsEditor = CodeMirror.fromTextArea(document.getElementById("js-editor"), {
            lineNumbers: true,
            mode: "javascript",
            theme: "material"
        });

        document.getElementById("run-button").addEventListener("click", function() {
            var htmlCode = htmlEditor.getValue();
            var cssCode = cssEditor.getValue();
            var jsCode = jsEditor.getValue();

            var output = document.getElementById("output-container");
            var documentContent = `






                    ${htmlCode}
                    ${jsCode}<\/script>


            `;

            output.srcdoc = documentContent;
        });


